import React, { useEffect } from "react";
import { connect } from "react-redux";
import * as actions from "../Actions/usuarios";
import { Grid,Paper,TableContainer,table, TableHead,TableRow,TableCell,TableBody } from "@material-ui/core";


const Usuarios = (props) => {
    useEffect(() => {
        props.fetchallUsuarios()
    },[])



    return (
        <Paper>
            <Grid container>
                <Grid item xs={6}>
                    <usuarioform />
                </Grid>

                <Grid item xs={6}>
                    <TableContainer>
                        <table>
                            <TableHead>
                                <TableRow>
                                    <TableCell>Usuario</TableCell>
                                    <TableCell>Senha</TableCell>
                                </TableRow>
                            </TableHead>
                            <TableBody>
                                {
                                    props.usuarioList.map((record,index)=>{
                                        return (<TableRow key={index}>
                                            <TableCell>{record.usuario}</TableCell>
                                            <TableCell>{record.senha}</TableCell>
                                        </TableRow>)
                                    })      
                                }
                            </TableBody>
                        </table>
                    </TableContainer>
                </Grid> 

            </Grid>
        </Paper>
    );
}

const mapStateToProps = state => ({
        // usuarioList: state.usuario.list
        usuarioList: state.usuario.list
})

const mapActionToProps = {
    fetchallUsuarios : actions.fetchall
}

export default connect(mapStateToProps,mapActionToProps)(Usuarios);
