import React, {useState} from 'react'
import { Grid, TextField, withStyles, FormControl, InputLabel, Select, MenuItem, Button, FormHelperText } from '@material-ui/core';
import useForm from "./useForm";
import { connect } from 'react-redux'

const styles = theme =>({
    root: {
        '& .MuiTextField-root': {
            margin: theme.spacing(1),
            minWidth: 230,
        }
    },
    formControl: {
        margin: theme.spacing(1),
        minWidth: 230,
    },
    smMargin:{
        margin: theme.spacing(1)
    }
})

const initialFieldValues ={
    usuario : '',
    senha : ''
}

const UsuariosForms = ({classes, ...props}) => {

    //validação de campos
const validate = (fieldValues = values) => {
    let temp = {}
    if('usuario' in fieldValues)
        temp.usuario = fieldValues.usuario? "" : "Campo Obrigatório."
    // if(!fieldValues.senha)
    //     temp.senha = fieldValues.senha? "" : "Campo Obrigatório."
    if('senha' in fieldValues)
    temp.senha = fieldValues.senha? "" : "Campo Obrigatório."
    setErrors({
        ...temp
    })

    if(fieldValues == values){
        return Object.values(temp).every(x => x == "")
    }

}
   
    const {
        values,
        setValues,
        errors,
        setErrors,
        handleInputChange
    } = useForm(initialFieldValues, validate)

    const handleSubmit = e =>{
        e.preventDefault()
        if(validate()){
            window.alert('OK')
        }
    }


    return (
    <form autoComplete="off" noValidate className={classes.root} onSubmit={handleSubmit}>
      <Grid container>
        <Grid Item xs ={6}>
            <TextField
                name= "usuario"
                variant = "outlined"
                label="Usuário"
                value={values.usuario}
                onChange={handleInputChange}
                {...(errors.usuario && { error: true, helperText: errors.usuario })}
                
            />
            <TextField
                name= "senha"
                variant = "outlined"
                label="Senha"
                type="password"
                value={values.senha}
                onChange={handleInputChange}
                {...(errors.senha && { error: true, helperText: errors.senha })}
            />
            <div>
                <button
                    variant="contained"
                    color="primary"
                    type="submit"
                    className={classes.smMargin}                
                >
                    Submit
                </button>
            </div>
        </Grid>
      </Grid>

    </form>
  )
}

export default withStyles(styles) (UsuariosForms)
