import React, { useState, useEffect } from 'react'
import { connect } from 'react-redux'
import * as actions from '../Actions/usuarios'
import UsuarioForms from './UsuarioForms'
import { Grid, Paper, TableContainer, table, TableHead, TableRow, TableCell, TableBody, withStyles, ButtonGroup } from '@material-ui/core'
import EditIcon from "@material-ui/icons/Edit";
import DeleteIcon from "@material-ui/icons/Delete";

const styles = theme => ({
  root: {
    '& . MuiTableCell-head': {
      fontSize: '1.25rem'
    }
  },
  paper: {
    margin: theme.spacing(2),
    padding: theme.spacing(2)
  }
})

const Usuarios = ({ classes, ...props }) => {
  const [currentId, setCurrentId] = useState(0)


  useEffect(() => {
    props.fetchAllUsuarios()
    // console.log(props.usuarioList.length)
  }, [])

  return (
    <Paper className={classes.paper}>
      <Grid container>
        <Grid item xs={6}>
          <UsuarioForms {...({ currentId, setCurrentId })} />
        </Grid>

        <Grid item xs={6}>
          <TableContainer>
            <table>
              <TableHead className={classes.root}>
                <TableRow>
                  <TableCell>Usuario</TableCell>
                  <TableCell>Senha</TableCell>
                  <tableCell></tableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {
                  props.usuarioList.map((record, index) => {
                    return (<TableRow key={index}>
                      <TableCell>{record.usuario}</TableCell>
                      <TableCell>{record.senha}</TableCell>
                      <tableCell>
                        <ButtonGroup variant="Text">
                          <button><EditIcon color ="primary"
                          oncClick={() => { setCurrentId(record.id) }} /> </button>
                          <button><DeleteIcon color ="second" /> </button>
                        </ButtonGroup>

                      </tableCell>
                    </TableRow>)
                  })
                }
              </TableBody>
            </table>
          </TableContainer>
        </Grid>

      </Grid>
    </Paper>
  )
}

const mapStateToProps = state => ({
  usuarioList: state.usuario.list
})

const mapActionToProps = {
  fetchAllUsuarios: actions.fetchAll
}

export default connect(mapStateToProps, mapActionToProps)(withStyles(styles)(Usuarios))
