import React, {useState} from 'react'
import { Grid, TextField, withStyles } from '@material-ui/core';
import useForm from "./useForm";

const styles = theme =>({
    root: {
        '& .MuiTextField-root': {
            margin: theme.spacing(1),
            minWidth: 230,
        }
    },
})

const initialFieldValues ={
    usuario :'',
    senha :'',
}

const UsuariosForms = ({classes, ...props}) => {
   
    const {
        values,
        setValues,
        handleInputChange
    } = useForm(initialFieldValues)

    return (
    <form autoComplete="off" noValidate className= {classes.root}>
      <Grid container>
        <Grid Item xs ={6}>
            <TextField
                name= "Usuario"
                variant = "outlined"
                label="UsuÃ¡rio"
                value={values.usuario}
                onChange={handleInputChange}
            />
            <TextField
                name= "Senha"
                variant = "outlined"
                label="Senha"
                type="password"
                value={values.senha}
                onChange={handleInputChange}
            />
            <div>
                <button
                variant="Contained"
                color="primary"
                type="Submit"
                >
                Submit
                </button>
            </div>
        </Grid>
      </Grid>

    </form>
  )
}

export default withStyles(styles) (UsuariosForms)
