import React, { useEffect } from "react";
import { connect } from "react-redux";
import * as actions from "../Actions/usuarios";
import UsuarioForms from "./UsuarioForms";
import { Grid,Paper,TableContainer,table, TableHead,TableRow,TableCell,TableBody,withStyles } from "@material-ui/core";

const styles = theme =>({
    paper :{
        margin: theme.spacing(2),
        padding:theme.spacing(2)
    }
})

const Usuarios = ({classes,...props}) => {
    useEffect(() => {
        props.fetchAllUsuarios()
    },[])

    return (
        <Paper className={classes.paper}>
            <Grid container>
                <Grid item xs={6}>
                    <UsuarioForms />
                </Grid>

                <Grid item xs={6}>
                    <TableContainer>
                        <table>
                            <TableHead>
                                <TableRow>
                                    <TableCell>Usuario</TableCell>
                                    <TableCell>Senha</TableCell>
                                </TableRow>
                            </TableHead>
                            <TableBody>
                                {
                                    props.usuarioList.map((record,index)=>{
                                        return (<TableRow key={index}>
                                            <TableCell>{record.usuario}</TableCell>
                                            <TableCell>{record.senha}</TableCell>
                                        </TableRow>)
                                    })
                                }
                            </TableBody>
                        </table>
                    </TableContainer>
                </Grid> 

            </Grid>
        </Paper>
    );
}

const mapStateToProps = state => ({
    usuarioList: state.usuario.list
})

const mapActionToProps = {
    fetchAllUsuarios : actions.fetchAll
}

export default connect(mapStateToProps, mapActionToProps)(withStyles(styles)(Usuarios));
