import api from "./api";

export const ACTION_TYPES = {
    CREATE: 'CREATE',
    UPDATE: 'UPDATE',
    DELETE: 'DELETE',
    FETCH_ALL: 'FETCH_ALL'
}

//funcao simplificada
export const fetchall = () => dispatch => {
    api.usuario()
    .then(response => {
            dispatch({
                type:ACTION_TYPES.FETCH_ALL,
                payload: response.data
            })
        }
    ) 
    .catch(err => console.log(err))   
}
