import React, {useState} from 'react'
import { Grid, TextField, withStyles, FormControl, InputLabel, Select, MenuItem, Button, FormHelperText } from '@material-ui/core';
import useForm from "./useForm";

const styles = theme =>({
    root: {
        '& .MuiTextField-root': {
            margin: theme.spacing(1),
            minWidth: 230,
        }
    },
    formControl: {
        margin: theme.spacing(1),
        minWidth: 230,
    },
    smMargin:{
        margin: theme.spacing(1)
    }
})

const initialFieldValues ={
    usuario : '',
    senha : ''
}

const UsuariosForms = ({classes, ...props}) => {

    const validate = () =>{
        let temp={}
        temp.usuario = values.usuario?"":"Campo Obrigatório."
        temp.senha = values.senha?"":"Campo Obrigatório."
        setErrors({
            ...temp
        })

        return Object.values(temp).every(x=> x=="")

    }
   
    const {
        values,
        setValues,
        errors,
        setErrors,
        handleInputChange
    } = useForm(initialFieldValues)

    const handleSubmit = e =>{
        e.preventDefault()
        if(validate()){
            window.alert('OK')
        }
    }


    return (
    <form autoComplete="off" noValidate className={classes.root} onSubmit={handleSubmit}>
      <Grid container>
        <Grid Item xs ={6}>
            <TextField
                name= "usuario"
                variant = "outlined"
                label="Usuário"
                value={values.usuario}
                onChange={handleInputChange}
                {...alert(errors.usuario && { error: true, helperText: errors.usuario})}
                
            />
            <TextField
                name= "senha"
                variant = "outlined"
                label="Senha"
                type="password"
                value={values.senha}
                onChange={handleInputChange}
            />
            <div>
                <button
                    variant="contained"
                    color="primary"
                    type="submit"
                    className={classes.smMargin}                
                >
                    Submit
                </button>
            </div>
        </Grid>
      </Grid>

    </form>
  )
}

export default withStyles(styles) (UsuariosForms)
