import { ACTION_TYPES } from "../Actions/usuarios";
const initialState = {
    usuario: usuario.list,
    list: []
}

export const usuario = (state = initialState, action) => {
    switch (action.type) {
        case ACTION_TYPES.FETCH_ALL:
            return {
                ...state,
                list:[...action.payload]
            }
    
        default:
        return state;
    }
}
